const {test} = require('@alexbosworth/tap');
const tinysecp = require('tiny-secp256k1');
const {Transaction} = require('bitcoinjs-lib');

const {decodePsbt} = require('./../../');
const {updatePsbt} = require('./../../');

// Test scenarios
const tests = {
  an_updater_updates_a_created_psbt: {
    args: {
      bip32_derivations: [
        {
          fingerprint: 'd90c6a4f',
          path: `m/0'/0'/0'`,
          public_key: '029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f',
        },
        {
          fingerprint: 'd90c6a4f',
          path: `m/0'/0'/1'`,
          public_key: '02dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d7',
        },
        {
          fingerprint: 'd90c6a4f',
          path: `m/0'/0'/2'`,
          public_key: '03089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc',
        },
        {
          fingerprint: 'd90c6a4f',
          path: `m/0'/0'/3'`,
          public_key: '023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e73',
        },
        {
          fingerprint: 'd90c6a4f',
          path: `m/0'/0'/4'`,
          public_key: '03a9a4c37f5996d3aa25dbac6b570af0650394492942460b354753ed9eeca58771',
        },
        {
          fingerprint: 'd90c6a4f',
          path: `m/0'/0'/5'`,
          public_key: '027f6399757d2eff55a136ad02c684b1838b6556e5f1b6b34282a94b6b50051096',
        },
      ],
      psbt: '',
      redeem_scripts: [
        '5221029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f2102dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d752ae',
        '00208c2353173743b595dfb4a07b72ba8e42e3797da74e87fe7d9d7497e3b2028903',
      ],
      transactions: [
        '',
        '',
      ],
      witness_scripts: [
        '522103089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc21023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e7352ae',
      ],
    },
    msg: 'An updater updates a created psbt',
    result: {
      psbt: '',
    },
  },

  an_update_which_adds_sighash_all: {
    args: {
      psbt: '',
      sighashes: [
        {
          id: '1dea7cd05979072a3578cab271c02244ea8a090bbb46aa680a65ecd027048d83',
          sighash: Transaction.SIGHASH_ALL,
          vout: 1,
        },
        {
          id: '75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858',
          sighash: Transaction.SIGHASH_ALL,
          vout: 0,
        },
      ],
    },
    msg: 'An updater which adds SIGHASH_ALL to a psbt',
    result: {
      psbt: '',
    },
  },
};

// Run the tests
Object.keys(tests).map(t => tests[t]).forEach(({args, err, msg, result}) => {
  return test(msg, async ({end, equal, throws, type}) => {
    const ecp = (await import('ecpair')).ECPairFactory(tinysecp);

    args.ecp = ecp;

    if (!!err) {
      throws(() => encodePsbt(args), new Error(err));

      return end();
    }

    const expected = decodePsbt({ecp, psbt: result.psbt});
    const {psbt} = updatePsbt(args);

    const updated = decodePsbt({ecp, psbt});

    equal(updated.pairs.length, expected.pairs.length, 'Map size is equal');

    expected.pairs.forEach((n, i) => {
      const got = updated.pairs[i];

      equal(got.type.toString('hex'), n.type.toString('hex'), 'Type match');
      equal(got.value.toString('hex'), n.value.toString('hex'), 'Value match');

      return;
    });

    equal(psbt, result.psbt);

    return end();
  });
});
